from django.db import modelsfrom users.model import User# Create your models here.class Notification(models.Model):    TYPES = [        ('email', 'Email'),        ('sms', 'SMS'),    ]    NOTIF_STATUS = [        ('pending', 'Pending'),        ('sent', 'Sent'),        ('failed', 'Failed'),    ]    notif_id = models.AutoField(primary_key=True)    user_id = models.ForeignKey(User, on_delete=models.PROTECT)    message = models.TextField()    type = models.CharField(max_length=15, choices=TYPES)    reference_table = models.CharField(max_length=50)    reference_id = models.IntegerField()    status = models.CharField(max_length=20, choices=NOTIF_STATUS)    created_at = models.DateTimeField(auto_now_add=True)    sent_at = models.DateTimeField(null=True, blank=True)    def __str__(self):        return f"Notif#{self.notif_id} to User:{self.user_id}"'''CREATE TABLE notifications (    notif_id INTEGER PRIMARY KEY AUTO_INCREMENT,    user_id INTEGER NOT NULL,    message TEXT NOT NULL,    type ENUM('email', 'sms'), -- Might be useful to add system type notifications as well    reference_table VARCHAR(50),    reference_id INTEGER,    status ENUM('pending', 'sent', 'failed') DEFAULT 'pending',    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,    sent_at DATETIME,    FOREIGN KEY (user_id) REFERENCES users(user_id));'''